import { Col, Row } from "react-bootstrap"
import { useState, useEffect } from "react"
import { toast } from "react-toastify"
import { api } from "../_service/api"
import { CardProduto } from "./CardProduto"

function ListaProdutos() {
    const [produtos, setProdutos] = useState([])

    useEffect(() => {
        buscarProdutos()
    }, [])


    async function buscarProdutos() {
        try {
            const response = await api.get("/ListaProdutos")
            setProdutos(response.data)
        } catch (error) {
            mensagemDeErro("Erro para buscar os produtos.")
        }
    }

    function mensagemDeErro(mensagem = "Mensagem de erro") {
        toast.error(mensagem, {
            position: "top-center",
            autoClose: 5000,
            hideProgressBar: false,
            closeOnClick: false,
            pauseOnHover: true,
            draggable: true,
            progress: undefined,
            theme: "light",
        })
    }

    return (
        <>
            <Row className="justify-content-md-center">
                <Col md={2}>
                    <h1>Lista Produtos</h1>
                </Col>
            </Row>
            <Row>
                {produtos.map((produto, index) => {
                    return (
                        <Col className="m-2" key={index}>
                            <CardProduto
                                nome={produto.title}
                                preco={produto.price}
                                imagem={produto.image}
                                descricao={produto.category}
                            />
                        </Col>
                    )

                })}
            </Row>

        </>

    )
}

export { ListaProdutos }
