import { createContext, useEffect, useState } from "react";
import { toast } from "react-toastify"


export const CarrinhoContext = createContext()

function CarrinhoContextProvider({ children }) {
const [listaProdutosCarrinho, setlistaProdutosCarrinho ] = useState([])
const [quantidadeProdutos, setQuantidadeProdutos] = useState(0) //Estado para atualizar o ícone do carrinho



useEffect(() => {
    buscarProdutosLocalStorage()
}, [])

    function buscarProdutosLocalStorage() {
        const produtosLocalStorage = localStorage.getItem("carrinho_produtos")

        if(produtosLocalStorage) {
            const produtos = JSON.parse(produtosLocalStorage)
            setlistaProdutosCarrinho(produtos)
            setQuantidadeProdutos(produtos.length) // Atualiza a contagem de produtos
        }
    }

function salvarProduto(lista) {
    setlistaProdutosCarrinho(lista)
    setQuantidadeProdutos(lista.length)
    localStorage.setItem("carrinho_produtos", JSON.stringify(lista))
}

const adicionarCarrinho = async (produto) => {

    const existeProduto = listaProdutosCarrinho.some(
        item => item.idProduto === produto.idProduto
    )

    if (existeProduto) {
        return toast.error("Produto já está no seu carrinho", {
                    position: "top-center",
                    autoClose: 5000,
                    hideProgressBar: false,
                    closeOnClick: false,
                    pauseOnHover: true,
                    draggable: true,
                    progress: undefined,
                    theme: "light",
                })
    }

    const listaAtualizada = [produto, ...listaProdutosCarrinho]
    salvarProduto(listaAtualizada)
}

function removerProduto(idProduto) {
    const listaAtualizada = listaProdutosCarrinho.filter(item => item.idProduto !== idProduto)

    salvarProduto(listaAtualizada)
}


    return (
        <CarrinhoContext.Provider value={{listaProdutosCarrinho, adicionarCarrinho, removerProduto, quantidadeProdutos}}>

        {children}            

        </CarrinhoContext.Provider>
    )
}

export {CarrinhoContextProvider}
